@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angular-form';
}

<form >
    <h2>Student Form</h2>
    <div class="form-group">    
        <label for="">Name</label>
        <input type="text">
    </div>
    <div>
        <button>Save</button>
    </div>


</form>
